import React, { Component } from 'react';

class SideWidget extends Component {

    componentDidMount() {

        for (let i = 0; i < this.props.Video.length; i++) {
            console.log(this.props.Video[i]);

            fetch('/Media/' + json[i].Video).then((response) => {

                return response.blob();
            }).then((blob) => {

                console.log(blob);

                const fr = new FileReader();

                fr.readAsDataURL(blob);
            });
        }
    }

    render() {
        return this.props.Videos.map((json, i) => (
            <tr key={i}>
                <td>
                    <p>{json.Video}</p>
                    <video width="400" controls>
                        <source src={blobData} type={fileType} />
                        Your browser does not support HTML5 video.
                    </video>
                </td>
            </tr>
        ));
    }
}

export default SideWidget;
