import React, { Component } from 'react';

class SideWidget extends Component {

    constructor(props) {
        super(props);
        this.state = {
            video: "No video yet",
        };
        this.addView = this.addView.bind(this);
        this.SaveData = this.SaveData.bind(this);
        this.onInitFs = this.onInitFs.bind(this);
        this.errorHandler = this.errorHandler.bind(this);

        this.data = { x: 42, s: "hello, world", d: new Date() };
        this.fileName = "my-download.json";
    }

    componentDidMount() {
    }

    addView(e) {
        e.preventDefault();
        console.log("Adding view");
        this.SaveData(this.data, this.fileName);
    }

    SaveData = (function () {
        console.log("Saving data");
    var a = document.createElement("a");
    document.body.appendChild(a);
    a.style = "display: none";
    return function (data, fileName) {
        var json = JSON.stringify(data),
            blob = new Blob([json], { type: "octet/stream" }),
            url = window.URL.createObjectURL(blob);
        a.href = url;
        a.download = fileName;
        a.click();
        window.URL.revokeObjectURL(url);
    };
}());

    onInitFs() {

    const fs = new FileReader();

    fs.root.getFile('log.txt', {}, function (fileEntry) {

        // Get a File object representing the file,
        // then use FileReader to read its contents.
        fileEntry.file(function (file) {
            var reader = new FileReader();

            reader.onloadend = function (e) {
                var txtArea = document.createElement('textarea');
                txtArea.value = this.result;
                document.body.appendChild(txtArea);
            };

            reader.readAsText(file);
        }, this.errorHandler);

    }, this.errorHandler);

}
    errorHandler(e) {
    var msg = '';
        /*
    switch (e.code) {
        case FileError.QUOTA_EXCEEDED_ERR:
            msg = 'QUOTA_EXCEEDED_ERR';
            break;
        case FileError.NOT_FOUND_ERR:
            msg = 'NOT_FOUND_ERR';
            break;
        case FileError.SECURITY_ERR:
            msg = 'SECURITY_ERR';
            break;
        case FileError.INVALID_MODIFICATION_ERR:
            msg = 'INVALID_MODIFICATION_ERR';
            break;
        case FileError.INVALID_STATE_ERR:
            msg = 'INVALID_STATE_ERR';
            break;
        default:
            msg = 'Unknown Error';
            break;
    };*/

    console.log('Error: ' + msg);
}

   

    render() {

        window.storageInfo.requestQuota(PERSISTENT, 1024 * 1024,
            function (grantedBytes) {
                window.requestFileSystem(window.PERSISTENT, grantedBytes, this.onInitFs, this.errorHandler);
            },
            this.errorHandler
        );


        return (
            <div>
                <form>
                    <input type="text" />
                    <button onClick={this.addView} type="submit">Save</button>
                </form>
            </div>
        );
    }
}

export default SideWidget;
