import React, { Component } from 'react';

class SideWidget extends Component {

    state = {
        blobData: [],
    }

    componentDidMount() {

        for (let i = 0; i < this.props.Videos.length; i++) {
            console.log(this.props.Videos[i]);

            fetch('/Media/' + this.props.Videos[i].Videos).then((response) => {

                return response.blob();
            }).then((blob) => {

                console.log(blob);

                let reader = new FileReader();


                //if reading completed
                reader.onload = e => {
                    let blobData = e.blob; //blob data
                    console.log(blobData);
                };

              //  this.state.blobData.push()

             //   this.thumbnail(blob);
            });
        }
    }
   

    

thumbnail(videoFromJson) {
    const canvas = document.getElementById('canvas');
    const video = document.getElementById(videoFromJson);
    canvas.getContext('2d').drawImage(video, 0, 0, video.videoWidth, video.videoHeight);
}

render() {
    return this.props.Videos.map((json, i) => (
        <tr key={i}>
            <td>
                <p>{json.Video}</p>
                <canvas id="canvas"></canvas>
                <video id={`${json.Video}`} width="80%" controls>
                    <source src={this.state.blobData[i]} />
                    Your browser does not support HTML5 video.
                    </video>
            </td>
        </tr>
    ));
}
}

export default SideWidget;
